def count(inp:str):
    inp = inp.replace(' ', '')
    if "+" in inp:
        return count(inp[:inp.find('+')]) + count(inp[inp.find('+')+1:])
    elif "*" in inp:
        return count(inp[:inp.find('*')]) * count(inp[inp.find('*')+1:])
    elif "**" in inp:
        return count(inp[:inp.find('**')]) ** count(inp[inp.find('**')+2:])
    else:
        return int(inp)
